package VariableStudy;

public class IntegerStudy {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			//1.Variable declaration
		String name;
		String name1;
		int RollNum;
		int Maths;
		int science;
		int totalmarks;
		
		
			//2.variable Initialization
		name="Reema";
		name1 ="Gurumukhi";
		RollNum =12;
		Maths =98;
		science =97;
		totalmarks =Maths +science;
	
		
		
			//3.Uasage (printing)
		System.out.println("My name is "+name+name1);
		System.out.println("My Roll number is "+RollNum);
		System.out.println("Total Marks for Maths and Science is "+totalmarks);
	}

}
